mapper.insert:

insert into cc_sys_log (log_id, log_title, log_type, 
  log_url, log_method, log_params, 
  log_exception, log_user_name, log_ip,
  log_ip_address, log_start_time, log_elapsed_time
  )
values (#{logId,jdbcType=INTEGER}, #{logTitle,jdbcType=VARCHAR}, #{logType,jdbcType=VARCHAR}, 
  #{logUrl,jdbcType=VARCHAR}, #{logMethod,jdbcType=VARCHAR}, #{logParams,jdbcType=VARCHAR}, 
  #{logException,jdbcType=VARCHAR}, #{logUserName,jdbcType=VARCHAR}, #{logIp,jdbcType=VARCHAR},
  #{logIpAddress,jdbcType=VARCHAR}, #{logStartTime,jdbcType=TIMESTAMP}, #{logElapsedTime,jdbcType=BIGINT}
  )

mapper.select:
select
from cc_sys_log
order by log_start_time DESC LIMIT #{start},#{limit}
